    getTableData2(isShow){
      //GET DATA OF TE CHANGE INFO
      var teChangeList = []

      let url = '/api/monitor/te/event',
        params={
          interval: '1d'
        }
      $.get(url, params, (res)=>{
        if(res.code == 0){
          this.tableData2 = []
          for(let i=0;i<res.data.length;i++){
            teChangeList.push({
              tunnelChange:res.data[i].oldTunnel[0]["tunnel-name"] + "=>" + res.data[i].newTunnel[0]["tunnel-name"],
              src:res.data[i].srcDevice,
              dst:res.data[i].dstDevice,
              reason:res.data[i].reason,
              time:res.data[i].time,
              vpnId:res.data[i].vpnId,
              SLA:res.data[i].color
            })
          }
          this.tableData2 = teChangeList;
           isShow && this.$message({
            showClose: true,
            message: 'TE变更列表更新成功',
            type: 'success'
          });
        }
      })
    }
